(123, 'Video Watch Page', 'video_watch', '<?\r\nglobal $data,$settings,$phrases,$scripturl,$links,$style,$global_align_x,$data_song;\r\n\r\n$url = $data[''url''];  \r\n\r\nopen_table("$data[name]");\r\n\r\n\r\nrun_player($url,''video_content'');   \r\n\r\n\r\n print "<center><br><br><table><tr>\r\n<td align=center><a href=\\"".$scripturl."/".str_replace(array(''{id}''),array($data[''id'']),$links[''video_download''])."\\"><img src=\\"$style[images]/save_mid.gif\\" alt=\\"$phrases[download]\\"></a></td>\r\n\r\n<td align=center>\r\n<a href=''http://www.facebook.com/sharer.php?u=".urlencode($scripturl."/".str_replace(array(''{id}''),array($data[''id'']),$links[''video_watch'']))."'' target=_blank title=''Share it on Facebook''><img src=''images/facebook_mid.gif'' alt=''Share it on Facebook'' border=0></a>\r\n</td>";\r\n\r\n//----- send -----//\r\nif($settings[''snd2friend_clip'']){\r\nprint "<td align=center><a href=\\"javascript:send($data[id],''video'');\\"><img src=''images/send_mid.gif'' border=0 title=''$phrases[send2friend]''></a></td>";\r\n}\r\n\r\n//------ fav -------\r\nif(check_member_login() && !$is_user_cp){\r\nprint "<td align=center><a href=\\"javascript:add_to_fav($data[id],''video'');\\"><img src=''images/fav_add_mid.gif'' title=''$phrases[add2favorite]'' border=0></a></td>";\r\n}\r\n\r\n//----------- report -------\r\nif($settings[''reports_enabled'']){\r\nprint "<td align=center>\r\n<a href=\\"javascript:;\\" onClick=\\"report($data[id],''video'');\\"><img src=\\"$style[images]/report.gif\\" title=\\"$phrases[report_do]\\" border=0></a></td>";  \r\n}\r\n//----------------------------\r\n\r\n\r\nprint "\r\n</table>\r\n</center>";\r\n \r\n//------- rating -----------\r\nprint "<br><br><center>";\r\nprint_rating(''video'',$data[''id''],$data[''rate'']);    \r\nprint "</center>";\r\n//-----------------------------\r\n\r\n \r\n   print "<br>\r\n        <img src=''$style[images]/add_date.gif''> &nbsp; <b>$phrases[add_date] : </b>".get_date($data[''date'']).