(111, 'Singer''s info Table', 'singer_info_table', '<?\r\nglobal $id,$data,$data_singer,$data_album,$phrases,$settings,$links,$global_align,$style,$global_align_x;\r\n\r\n$singer_fields = get_singers_fields_sets();\r\n\r\n\r\nopen_table();\r\nprint "<table width=100%><tr><td>";\r\n\r\n//------- Singer ----------//\r\n//if(!$data_album[''name'']){\r\nprint "<table width=100%><tr><td width=20% valign=top><img src=\\"".get_image($data_singer[''img''])."\\" border=0 title=\\"$data_singer[name]\\"></td>\r\n<td valign=top> \r\n<span class=title>$data_singer[name]</span><br><br>";\r\n\r\nif($data_singer[''content'']){\r\nprint "$data_singer[content] <br><br>";\r\n}\r\n\r\nprint "\r\n<span><img src=''$style[images]/add_date.gif''>&nbsp;<b>$phrases[last_update]  : </b>".iif($data_singer[''last_update''],get_date($data_singer[''last_update'']),$phrases[''not_available''])."</span><br>\r\n\r\n<span><img src=''$style[images]/views.gif''>&nbsp;<b>$phrases[visits] : </b> $data_singer[views]</span> <br>";\r\n\r\n\r\n//--------- fields -------------\r\nforeach($singer_fields as $field){\r\n$value = $data["field_".$field[''id'']];\r\nif($value){\r\nprint "<span><img src=''$style[images]/info.gif''> &nbsp;<b>$field[name] : </b> ".iif($field[''search''],"<a href=\\"search.php?op=singers&field_id=$field[id]&keyword=".$value."\\">").$value.iif($field[''search''],"</a>")."</span><br>";\r\n}\r\n} \r\n//-------------------------------\r\n\r\n\r\nprint "<br>";\r\n      \r\n\r\n\r\n\r\nprint_rating(''singer'',$data_singer[''id''],$data_singer[''rate'']);    \r\n\r\nprint " </td></tr></table>";\r\n//}\r\n//-------- Album ---------//\r\nif($data_album[''name'']){\r\n\r\n$album_fields = get_albums_fields_sets();\r\n\r\n\r\nprint "<hr class=''separate_line'' size=1 width=\\"40%\\" align=\\"$global_align\\">\r\n<table width=100%>\r\n<tr><td width=20% valign=top><img src=\\"".get_image($data_album[''img''])."\\" border=0 title=\\"$data_singer[name] - $data_album[name]\\"></td>\r\n<td valign=top><s