(18, 'Browse Songs', 'browse_songs', '<?\r\nglobal $data,$song_ext,$data_singer,$op,$settings,$phrases,$lyrics_count,$action,$is_user_cp,$urls_sets,$urls_data,$songs_fields_sets,$links,$is_member,$orderby,$videos_count,$style,$no_singer_name,$tr_class,$scripturl;\r\n\r\n\r\nprint "<div class=''song_div'' searchtext=\\"$data[name]\\">\r\n<table width=100%><tr class=''$tr_class''>\r\n<td>";\r\nif($settings[''songs_multi_select''] && CFN != "singer_overview.php"){\r\nprint "<input type=''checkbox'' name=\\"song_id[]\\" value=\\"$data[id]\\">";\r\n}\r\nprint "<img src=''images/song.gif'' border=0> &nbsp; <a href=\\"".str_replace(array(''{cat}'',''{id}''),array($settings[''default_url_id''],$data[''id'']),$links[''song_listen''])."\\" title=\\"$data[name]\\">$data[name]</a>";\r\n\r\nif($data[''c_ext'']){\r\nprint "&nbsp;&nbsp; <a href=\\"search.php?op=songs&c_ext=1&keyword=$data[c_ext]\\" title=\\"$data[c_ext]\\"><font color=''#808080''><i>$data[c_ext]</i></font></a>";\r\n}\r\n\r\n\r\nif($song_ext[''id'']){\r\nprint "&nbsp;&nbsp; <a href=\\"search.php?op=songs&ext=$song_ext[id]&keyword=$song_ext[name]\\" title=\\"$song_ext[name]\\"><font color=''#D20000''><i>$song_ext[name]</i></font></a>";\r\n}\r\n\r\nif($orderby=="id"){\r\nprint "&nbsp;&nbsp; <font color=''#000000''><i>".get_date($data[''date''])."</i></font>";\r\n}\r\n\r\nif($orderby=="rate"){\r\nprint "&nbsp;&nbsp; <font color=''#000000''><i>$data[rate]/ 5 </i></font>";\r\n}\r\n\r\n\r\n//--------- fields ---------------\r\n$br_printed = false;\r\n\r\nforeach($songs_fields_sets as $field){\r\n$value = $data["field_".$field[''id'']];\r\nif($value){\r\nif(!$br_printed){print "<br>";$br_printed=true;}\r\nprint "<b>$field[name] : </b> ".iif($field[''search''],"<a href=\\"search.php?op=songs&field_id=$field[id]&keyword=".$value."\\">").$value.iif($field[''search''],"</a>")."&nbsp";\r\n}\r\n} \r\n//-------------------------------------\r\n\r\nprint "</td>";\r\n\r\nif($data_singer[''name''] && !$no_singer_name){\r\nprint "<t