(136, 'Photo View', 'photo_view', '<?\r\nglobal $data,$global_align,$global_align_x,$list,$prev_index,$next_index,$phrases,$in_this_photo,$style,$links;\r\n\r\n\r\n print "<table width=100%><tr>\r\n       <td width=''33%'' align=''$global_align''>";\r\n       \r\n       if($list[$prev_index][''id'']){ \r\n      \r\n       print "<a href=\\"".str_replace("{id}",$list[$prev_index][''id''],$links[''singer_photo''])."\\"><img src=\\"images/arrw_$global_align.gif\\" title=\\"$phrases[prev_photo]\\" border=0><br>\r\n       $phrases[prev_photo]</a>";\r\n       }\r\n       \r\n       print "</td>\r\n       <td width=''33%'' align=''center''><a href=\\"$data[img]\\" target=_blank>\r\n       <img src=\\"images/full_size.gif\\" title=\\"$phrases[full_photo_size]\\"><br>$phrases[full_photo_size]</a></td>  \r\n       <td width=''33%'' align=''$global_align_x''>";\r\n    \r\n     if($list[$next_index][''id'']){ \r\n      $next_url = str_replace("{id}",$list[$next_index][''id''],$links[''singer_photo'']); \r\n       print "<a href=\\"".$next_url."\\"><img src=\\"images/arrw_".$global_align_x.".gif\\" title=\\"$phrases[next_photo]\\" border=0><br>\r\n       $phrases[next_photo]</a>";\r\n     }else{\r\n        $next_url = str_replace("{id}",$list[0][''id''],$links[''singer_photo'']);\r\n       }\r\n     \r\n       print "</td>  \r\n       </tr></table><br><br>";\r\n       \r\n \r\n         print "<center><a href=\\"javascript:;\\" onClick=\\"window.location=scripturl+''/$next_url'';\\"><img src=\\"$data[img_resized]\\" title=\\"$data[name]\\" border=0></a><br><br>";\r\n       print_rating(''singer_photo'',$data[''id''],$data[''rate'']);\r\n       print "</center>";\r\n       \r\n       \r\n       print iif($data[''name''],"<br><br> <img src=''$style[images]/info.gif''> &nbsp; $data[name]");\r\n       \r\n       print "<br>\r\n        <img src=''$style[images]/add_date.gif''> &nbsp; <b>$phrases[add_date] : </b>".get_date($data[''date''])."<br>";\r\n        print "<img src=''$style[images]/views.gif''> &nbsp; <b>$phrases[views] : </b>$data[views]"; \r\n        \r\n        \r\n        \r\n      \r\n        \r\n      \r\n         print iif($in_this_photo,"<br><br> <img src=''$style[images]/in_this_photo.gif''> &nbsp; <b>$phrases[in_this_photo] : </b> $in_this_photo");     \r\n      //--------------------------//   \r\n         \r\n       print "<br><br>\r\n\r\n<div align=''$global_align_x''>\r\n<a href=''http://www.facebook.com/sharer.php?u=".urlencode("http://".$_SERVER[''HTTP_HOST''].$_SERVER[''REQUEST_URI''])."'' target=_blank title=''Share it on Facebook''><img src=''images/facebook_mid.gif'' alt=''Share it on Facebook'' border=0></a>\r\n</div>";\r\n?>', 1, 1);
